INSERT INTO diaries (title, content, created_at, location_name, latitude, longitude, address, city, province, country, images, is_public, allow_comments, view_count, like_count, comment_count, user_id, tags)
VALUES
('西湖一日游', '今天游览了杭州西湖，风景如画，特别是断桥残雪和雷峰塔的景色令人难忘。湖边的柳树随风摇曳，给人一种宁静的感觉。中午在楼外楼品尝了当地特色菜，东坡肉和西湖醋鱼都非常美味。下午乘船游湖，欣赏了三潭印月的美景。西湖果然名不虚传，是个放松心情的好地方。', NOW() - INTERVAL 10 DAY, '西湖', 30.2590, 120.1490, '浙江省杭州市西湖区', '杭州', '浙江', '中国', '["diary_xh1.jpg", "diary_xh2.jpg"]', 1, 1, 120, 45, 8, 2, '["自然风光", "文化", "摄影"]'),
('故宫一日游', '参观了北京故宫，紫禁城的建筑宏伟壮观，金碧辉煌。了解了很多明清历史和宫廷文化，特别是太和殿、乾清宫等主要建筑给人印象深刻。虽然游客很多，但秩序井然。建议早上去，可以避开高峰期。', NOW() - INTERVAL 15 DAY, '故宫', 39.9163, 116.3972, '北京市东城区景山前街4号', '北京', '北京', '中国', '["diary_gg1.jpg", "diary_gg2.jpg", "diary_gg3.jpg"]', 1, 1, 200, 80, 15, 4, '["历史", "古迹", "博物馆"]'),
('重庆火锅体验', '在重庆尝试了正宗的重庆火锅，麻辣鲜香，让人回味无穷。特别是毛肚、鸭肠等食材新鲜美味。火锅店氛围热闹，服务也很周到。饭后漫步解放碑步行街，感受山城夜景的魅力。', NOW() - INTERVAL 5 DAY, '解放碑', 29.5583, 106.5684, '重庆市渝中区解放碑步行街', '重庆', '重庆', '中国', '["diary_cq1.jpg", "diary_cq2.jpg"]', 1, 1, 150, 60, 12, 3, '["美食", "城市", "夜景"]'),
('黄山徒步之旅', '历时两天徒步黄山，欣赏了日出、云海和奇松怪石。虽然路途艰辛，但沿途风景绝美，特别是光明顶的全景和迎客松的雄姿令人难忘。住在山顶的宾馆，晚上看星空也是一种享受。建议做好充分准备，带足水和食物。', NOW() - INTERVAL 20 DAY, '黄山', 30.1333, 118.1667, '安徽省黄山市黄山区', '黄山', '安徽', '中国', '["diary_hs1.jpg", "diary_hs2.jpg", "diary_hs3.jpg"]', 1, 1, 180, 75, 20, 2, '["徒步", "自然风光", "摄影"]'),
('上海迪士尼一日游', '带孩子去了上海迪士尼，园区很大，设施很新。最喜欢加勒比海盗和创极速光轮这两个项目。米奇大巡游和城堡烟火秀也很精彩。建议提前规划路线，使用快速通行证可以节省排队时间。', NOW() - INTERVAL 8 DAY, '上海迪士尼乐园', 31.1440, 121.6570, '上海市浦东新区申迪西路753号', '上海', '上海', '中国', '["diary_disney1.jpg", "diary_disney2.jpg"]', 1, 1, 160, 65, 18, 5, '["主题公园", "家庭", "娱乐"]'),
('西安美食之旅', '在西安体验了各种陕西美食，肉夹馍、羊肉泡馍、biangbiang面都很地道。回民街的小吃种类繁多，每一种都值得尝试。参观了兵马俑和古城墙，感受历史文化的厚重。晚上的大唐不夜城灯光秀也很震撼。', NOW() - INTERVAL 12 DAY, '西安回民街', 34.2655, 108.9508, '陕西省西安市碑林区东大街', '西安', '陕西', '中国', '["diary_xa1.jpg", "diary_xa2.jpg"]', 1, 1, 140, 55, 10, 3, '["美食", "历史", "文化"]');
